import React from "react";
import {StyleSheet} from 'react-native';
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import DashboardScreen from "../../features/account/screens/Dashboard";
import People from "../../features/account/screens/People";
import Companies from "../../features/account/screens/Companies";
import Settings from "../../features/account/screens/Settings";
import Icon from 'react-native-vector-icons/Feather';

const Tab = createBottomTabNavigator();
const BottomTabNavigator = () => {
  return (
    <View style={{backgroundColor:'red'}}>
      <View style={styles.container}>
        <Text>Left</Text>
        <Text>TopBar</Text>
        <Text>Right</Text>
      </View>
      <Tab.Navigator screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;
          if (route.name === 'DashBoard') {
            iconName = focused
              ? 'home'
              : 'home-outline';
          } else if (route.name === 'People') {
            iconName = focused ? 'users' : 'users';
          }
          else if (route.name === 'Companies') {
            iconName = focused ? 'buildings' : 'buildings';
          }
          else if (route.name === 'More') {
            iconName = focused ? 'more-vertical' : 'more-vertical';
          }
          return <Icon name={iconName} size={size} color={color} />;
        },
      })}
      >
        <Tab.Screen name="DashBoard" component={DashboardScreen} />
        <Tab.Screen name="People" component={People} />
        <Tab.Screen name="Companies" component={Companies} />
        <Tab.Screen name="More" component={Settings} />
      </Tab.Navigator>
      </View>
  );
};


const styles = StyleSheet.create({
  container: {
    alignSelf: 'stretch',
    height: 52,
    flexDirection: 'row', // row
    backgroundColor: 'yellow',
    alignItems: 'center',
    justifyContent: 'space-between', // center, space-around
    paddingLeft: 10,
    paddingRight: 10
  }
});
export default BottomTabNavigator;
